function main(e){$("select[name='locale']").change(function(e){Api.Locale.change(this.value,function(){location.reload()})}),$(".js-split-pane").each(function(){Split($(this).children().toArray(),{gutterSize:6,sizes:[].slice.call(this.children).map(e=>+e.getAttribute("data-size"))})}),Accordion.init(),$("body").css("opacity",1)}var Api={PREFIX:"api",makeUrl:function(e){var i="/"+Api.PREFIX+"/";return(e?i+e+"/":i).replace(/\/{2,}/g,"/")},Locale:{change:function(e,i){axios.get(Api.makeUrl(),{params:{locale:e}}).then(i)}},Sidebar:{onToggle:function(){}}};document.addEventListener("DOMContentLoaded",main);
