function main(e){$("select[name='locale']").change(function(e){Api.Locale.change(this.value,function(){location.reload()})}),$(".js-split-pane").each(function(){Split($(this).children().toArray(),{gutterSize:6,sizes:[].slice.call(this.children).map(e=>+e.getAttribute("data-size"))})})}var Api={PREFIX:"api",makeUrl:function(e){var a="/"+Api.PREFIX+"/";return(e?a+e+"/":a).replace(/\/{2,}/g,"/")},Locale:{change:function(e,a){axios.get(Api.makeUrl(),{params:{locale:e}}).then(a)}}};document.addEventListener("DOMContentLoaded",main);
